INSERT INTO `tb_notifikasi_email` VALUES (1,2,'desistrom0@gmail.com','EWEWEWE','<p>EEWEWEWEWEWE</p>'),(2,1,'desistrom1@gmail.com','Register','<p>Registrasi berhasil</p>\r\n\r\n<ul>\r\n <li>Nama : M. Puji Junaedi</li>\r\n <li>Kelas : 12 mi 2</li>\r\n <li>absen 2</li>\r\n</ul>'),(3,1,'desistrom1@gmail.com','Registrasi','<p>Registrasi berhasil</p>\r\n\r\n<ul>\r\n <li>Nama : M. Puji Junaedi</li>\r\n <li>Kelas : 12 mi 2</li>\r\n <li>absen 2</li>\r\n</ul>'),(4,2,'desistrom1@gmail.com','Permintaan sedang di proses','<p>aku adalah</p>\r\n\r\n<p>Contents</p>\r\n\r\n<p>  [hide] </p>\r\n\r\n<ul>\r\n <li>1Etymology</li>\r\n <li>2History\r\n <ul>\r\n  <li>2.1Pre-Internet</li>\r\n  <li>2.2History</li>\r\n </ul>\r\n </li>\r\n <li>3In different media\r\n <ul>\r\n  <li>3.1Email</li>\r\n  <li>3.2Instant messaging</li>\r\n  <li>3.3Newsgroup and forum</li>\r\n  <li>3.4Mobile phone</li>\r\n  <li>3.5Social networking spam</li>\r\n  <li>3.6Social spam</li>\r\n  <li>3.7Online game messaging</li>\r\n  <li>3.8Spam targeting search engines (spamdexing)</li>\r\n  <li>3.9Blog, wiki, and guestbook</li>\r\n  <li>3.10Spam targeting video sharing sites</li>\r\n  <li>3.11SPIT</li>\r\n  <li>3.12Academic search</li>\r\n  <li>3.13</li>\r\n </ul>\r\n </li>\r\n</ul>'),(5,1,'desistrom1@gmail.com','Registrasi','<p>Data Anda</p>\r\n\r\n<ul>\r\n <li>Nama : Junaedi</li>\r\n <li>Kelas : 4.1/22</li>\r\n <li>Alamat : Bojonegoro</li>\r\n</ul>'),(6,1,'desistrom1@gmail.com','Registrasi Berhasil','<ul>\r\n <li>Nama : Dika</li>\r\n <li>Alamat : Panjunan</li>\r\n <li>Asal : Bojonegoro</li>\r\n</ul>'),(7,1,'desistrom1@gmail.com','Registrasi Berhasil','<ul>\r\n <li>Nama : Dika</li>\r\n <li>Alamat : Panjunan</li>\r\n <li>Asal : Bojonegoro</li>\r\n</ul>'),(8,1,'desistrom1@gmail.com','Makan Bang','<p>Makan</p>');
